mapper.select:
SELECT * FROM( SELECT doctor_number, doctor_name, doctor_email, doctor_password, doctor_nickname, doctor_post_code, doctor_address, doctor_detail_address, doctor_extra_address, doctor_license, doctor_major, doctor_status, TO_CHAR(doctor_join_date, 'YYYY.MM.DD') AS doctor_join_date, ROW_NUMBER() OVER (ORDER BY doctor_number DESC) AS rnum FROM MAMADOC_DOCTOR
WHERE doctor_name LIKE '%' || #{searchVO.keyword} || '%' AND doctor_status = 1 ORDER BY doctor_number DESC
WHERE doctor_nickname LIKE '%' || #{searchVO.keyword} || '%' AND doctor_status = 1 ORDER BY doctor_number DESC
WHERE doctor_email LIKE '%' || #{searchVO.keyword} || '%' AND doctor_status = 1 ORDER BY doctor_number DESC
WHERE doctor_status = 1 ORDER BY doctor_number DESC
)temp WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT * FROM( SELECT doctor_number, doctor_name, doctor_email, doctor_password, doctor_nickname, doctor_post_code, doctor_address, doctor_detail_address, doctor_extra_address, doctor_license, doctor_major, doctor_status, TO_CHAR(doctor_join_date, 'YYYY.MM.DD') AS doctor_join_date, ROW_NUMBER() OVER (ORDER BY doctor_number DESC) AS rnum FROM MAMADOC_DOCTOR
WHERE doctor_name LIKE '%' || #{searchVO.keyword} || '%' AND doctor_status = 0 ORDER BY doctor_number DESC
WHERE doctor_nickname LIKE '%' || #{searchVO.keyword} || '%' AND doctor_status = 0 ORDER BY doctor_number DESC
WHERE doctor_email LIKE '%' || #{searchVO.keyword} || '%' AND doctor_status = 0 ORDER BY doctor_number DESC
WHERE doctor_status = 0 ORDER BY doctor_number DESC
)temp WHERE rnum BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE MAMADOC_DOCTOR
SET doctor_status = 1
WHERE doctor_number = #{doctorNumber}
