mapper.select:
SELECT nutrients_number, admin_number, nutrients_name, nutrients_effect, nutrients_info, TO_CHAR(nutrients_date, 'YYYY.MM.DD') AS nutrients_date
FROM MAMADOC_NUTRIENTS
WHERE nutrients_number = #{nutrientsNumber}

mapper.update:
UPDATE MAMADOC_NUTRIENTS
SET nutrients_name = #{nutrientsName},
	nutrients_effect = #{nutrientsEffect},
	nutrients_info = #{nutrientsInfo}
WHERE nutrients_number = #{nutrientsNumber}

mapper.select:
SELECT * FROM( SELECT N.nutrients_number, N.admin_number, N.nutrients_name, N.nutrients_effect, N.nutrients_info, TO_CHAR(N.nutrients_date, 'YYYY.MM.DD') AS nutrients_date, F.nufile_number, F.nufile_path, F.nufile_uuid, F.nufile_name, F.nufile_type, ROW_NUMBER() OVER (ORDER BY N.nutrients_number DESC) AS rnum FROM MAMADOC_NUTRIENTS N JOIN MAMADOC_NUFILE F ON N.nutrients_number = F.nutrients_number
WHERE N.nutrients_name LIKE '%' || #{searchVO.keyword} || '%' ORDER BY N.nutrients_number DESC
WHERE N.nutrients_effect LIKE '%' || #{searchVO.keyword} || '%' ORDER BY N.nutrients_number DESC
ORDER BY N.nutrients_number DESC
) temp WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT * FROM( SELECT nutrients_number, admin_number, nutrients_name, nutrients_effect, nutrients_info, TO_CHAR(nutrients_date, 'YYYY.MM.DD') AS nutrients_date, ROW_NUMBER() OVER (ORDER BY nutrients_number DESC) AS rnum FROM MAMADOC_NUTRIENTS
WHERE nutrients_name LIKE '%' || #{searchVO.keyword} || '%' ORDER BY nutrients_number DESC
WHERE nutrients_effect LIKE '%' || #{searchVO.keyword} || '%' ORDER BY nutrients_number DESC
ORDER BY nutrients_number DESC
) temp WHERE rnum BETWEEN #{startRow} AND #{endRow}
