mapper.select:
SELECT * FROM( SELECT C.clinic_number, C.member_number, C.clinic_title, C.clinic_content, C.clinic_date, M.member_name, M.member_nickname, ROW_NUMBER() OVER (ORDER BY clinic_number DESC) AS rnum FROM MAMADOC_CLINIC C JOIN MAMADOC_MEMBER M ON C.member_number = M.member_number
WHERE clinic_title LIKE '%' || #{searchVO.keyword} || '%' ORDER BY clinic_number
WHERE member_nickname LIKE '%' || #{searchVO.keyword} || '%' ORDER BY clinic_number
ORDER BY clinic_number
) temp WHERE rnum BETWEEN #{startRow} AND #{endRow}
