mapper.select:
SELECT * FROM( SELECT D.doctor_number, D.doctor_name, D.doctor_nickname, P.precaution_number, P.doctor_number AS precaution_doctor_number, P.precaution_title, P.precaution_content, P.precaution_date, ROW_NUMBER() OVER (ORDER BY precaution_number DESC) AS rnum FROM MAMADOC_DOCTOR D JOIN MAMADOC_PRECAUTION P ON D.doctor_number = P.doctor_number
WHERE precaution_title LIKE '%' || #{searchVO.keyword} || '%' ORDER BY precaution_number
WHERE doctor_nickname LIKE '%' || #{searchVO.keyword} || '%' ORDER BY precaution_number
ORDER BY precaution_number
) temp WHERE rnum BETWEEN #{startRow} AND #{endRow}
